// 注意，这里的 this.form78ed8eb7model 需要替换成表单容器的变量
// 可以使用 lesscode. 来唤起快捷输入，然后选择表单容器的变量
const formData = this.form0181963dmodel || {}
const formKeys = Object.keys(formData)
const defaultMap = {
    '[object String]': '',
    '[object Date]': '',
    '[object Number]': 0,
    '[object Boolean]': false,
    '[object Object]': {},
    '[object Array]': [],
    '[object Function]': () => { }
}
formKeys.forEach((formKey) => {
    const value = formData[formKey]
    const valueType = Object.prototype.toString.apply(value)
    if (defaultMap.hasOwnProperty(valueType)) {
        formData[formKey] = defaultMap[valueType]
    }
})
